// render dynamic gallery data
galleryData.data.forEach(gal => $('.gallery--tile-container').append("<div class='grid-col-12 tablet-lg:grid-col-3 tablet:grid-col-4 display-flex flex-align-stretch modal-toggle'>" + "<div class='usa-card' >" + "<div class='usa-card__container text-primary' >" + "<a class='gallery-tags' href='#gallery-modal' aria-controls='gallery-modal' data-open-modal data-src='" + gal.img + "' data-name='" + gal.name + "' data-department='" + gal.department + "' data-title='" + gal.title + "' data-organization='" + gal.organization + "' data-description='" + gal.description + "'>" + "<div class='usa-card__media usa-card__media--inset' >" + "<div class='usa-card__img bg-white' >" + "<img class='radius-lg lazy' data-src='" + gal.img + "'alt='" + gal.name + "' />" + "</div>" + "</div>" + "<div class='usa-card__body' >" + "<span class='gallery-name padding-y-1'>" + gal.name + "</span>" + "<span>" + gal.department + "</span>" + "<span>" + gal.title + "</span>" + "<span class='gallery-org' >" + gal.organization + "</span>" + "</a>" + "</div>" + "</div>" + "</div>" + "</div>")); 

// render dynamic modal data
$("a.gallery-tags").click(function () {
    $('.modal-img').attr('src', $(this).data('src'));
    $('.modal-img').attr("alt", $(this).data('name'));
    $('.modal-name').html($(this).data('name'));
    $('.modal-department').html($(this).data('department'));
    $('.modal-title').html($(this).data('title'));
    $('.modal-organization').html($(this).data('organization'));
    $('.modal-description').html($(this).data('description'));
});

// remove empty elements
$(".publication-list__item-container span:empty").remove();
$(".publication-list__item-container p:empty").remove();
